frappe.ui.form.on('Sales Invoice', {
  refresh(frm) {
    var template = '';
    if (frm.doc.__islocal) {
      template = '<img src="" />';
	  frm.set_df_property('qr_preview', 'options', frappe.render_template(template));
      frm.refresh_field('qr_preview');
    } else {
      if (frm.doc.qr_code) {
        template = '<img src="' + frm.doc.qr_code + '" width="240px"/>';
	    frm.set_df_property('qr_preview', 'options', frappe.render_template(template));
        frm.refresh_field('qr_preview');
      }        
    }
  },
  onload(frm) {
    var template = '';
    if (frm.doc.__islocal) {
      template = '<img src="" />';
	  frm.set_df_property('qr_preview', 'options', frappe.render_template(template));
      frm.refresh_field('qr_preview');
    } else {
      if (frm.doc.qr_code) {
        template = '<img src="' + frm.doc.qr_code + '" width="240px"/>';
	    frm.set_df_property('qr_preview', 'options', frappe.render_template(template));
        frm.refresh_field('qr_preview');
      }    
    }
  }
});

